import React, { useState } from "react";
import { auth } from "../firebase";
import {
  Box, Paper, TextField, Button, Typography, Avatar, Stack
} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";

export default function ChatInterface() {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState("");

  const handleSend = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;
    try {
      const idToken = await auth.currentUser.getIdToken();
      const response = await fetch("http://localhost:8000/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${idToken}`
        },
        body: JSON.stringify({
          message,
          session_id: "unique-session-id"
        })
      });
      const data = await response.json();
      setMessages(prev => [
        ...prev,
        { text: message, isUser: true },
        { text: data.response, isUser: false }
      ]);
      setMessage("");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <Box sx={{ maxWidth: 600, margin: "40px auto" }}>
      <Paper elevation={3} sx={{ p: 2, minHeight: 400 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>AI Booking Assistant</Typography>
        <Stack spacing={2} sx={{ mb: 2, maxHeight: 300, overflowY: "auto" }}>
          {messages.map((msg, idx) => (
            <Box
              key={idx}
              sx={{
                display: "flex",
                justifyContent: msg.isUser ? "flex-end" : "flex-start"
              }}
            >
              <Box
                sx={{
                  bgcolor: msg.isUser ? "primary.main" : "grey.200",
                  color: msg.isUser ? "white" : "black",
                  px: 2, py: 1, borderRadius: 2, maxWidth: "70%"
                }}
              >
                {msg.text}
              </Box>
            </Box>
          ))}
        </Stack>
        <Box component="form" onSubmit={handleSend} sx={{ display: "flex", gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type your message..."
            value={message}
            onChange={e => setMessage(e.target.value)}
          />
          <Button type="submit" variant="contained" endIcon={<SendIcon />}>
            Send
          </Button>
        </Box>
        {error && <Typography color="error" sx={{ mt: 1 }}>{error}</Typography>}
      </Paper>
    </Box>
  );
}
