import React, { useState } from "react";
import { auth } from "../firebase";
import { Box, Paper, TextField, Button, Typography, Stack } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import './ChatInterface.css';
import { signOut } from "firebase/auth";
import { v4 as uuidv4 } from "uuid"; 
function SignOutButton() {
  return <button onClick={() => signOut(auth)}>Sign Out</button>;
}

export default function ChatInterface() {
  // State must be inside the component
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState("");

  // Handler function must be inside the component
  const handleSend = async (e) => {
  e.preventDefault();
  if (!message.trim()) return;

  const userMessage = message;

  // Immediately show user message
  setMessages(prev => [...prev, { text: userMessage, isUser: true }]);

  // Clear input & error
  setMessage("");
  setError("");

  try {
    const idToken = await auth.currentUser.getIdToken();

    const response = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${idToken}`
      },
      body: JSON.stringify({
        message: userMessage,
        session_id: "unique-session-id"
      })
    });

    const data = await response.json();

    if (response.ok) {
      setMessages(prev => [...prev, { text: data.response, isUser: false }]);
    } else {
      throw new Error(data.detail || "Something went wrong.");
    }

  } catch (err) {
    setMessages(prev => [
      ...prev,
      { text: "⚠️ GPT failed to respond. Try again.", isUser: false }
    ]);
    setError(err.message);
  }
};

      

  // JSX uses the variables and functions defined above
  return (
    <Box className="chat-container">
      <Paper className="chat-paper">
        <Typography variant="h5" sx={{ mb: 2 }}>AI Booking Assistant</Typography>
        <SignOutButton />
        <Stack className="messages-container">
          {messages.map((msg, idx) => (
            <Box
              key={idx}
              className={`message-box ${msg.isUser ? 'message-user' : 'message-bot'}`}
            >
              <Box className={`message-bubble ${msg.isUser ? 'user-bubble' : 'bot-bubble'}`}>
                {msg.text}
              </Box>
            </Box>
          ))}
        </Stack>
        <Box component="form" onSubmit={handleSend} sx={{ display: "flex", gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type your message..."
            value={message}
            onChange={e => setMessage(e.target.value)}
          />
          <Button type="submit" variant="contained" endIcon={<SendIcon />}>
            Send
          </Button>
        </Box>
        {error && <Typography color="error" sx={{ mt: 1 }}>{error}</Typography>}
      </Paper>
    </Box>
  );
}
