// frontend/src/components/ChatInterface.js
import React, { useState } from "react";
import { auth } from "../firebase";
import { Box, Paper, TextField, Button, Typography, Stack } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import './ChatInterface.css';

export default function ChatInterface() {
  // ... existing state and logic remains the same ...

  return (
    <Box className="chat-container">
      <Paper className="chat-paper" elevation={3}>
        <Typography variant="h5" sx={{ mb: 2, color: 'purple' }}>AI Booking Assistant</Typography>
        <Stack className="messages-container">
          {messages.map((msg, idx) => (
            <Box
              key={idx}
              className={`message-box ${msg.isUser ? 'message-user' : 'message-bot'}`}
              sx={{ 
                marginBottom: 2,
                '&:last-child': { marginBottom: 0 }
              }}
            >
              <Box
                className={`message-bubble ${msg.isUser ? 'user-bubble' : 'bot-bubble'}`}
                sx={{
                  fontSize: '1.1rem',
                  lineHeight: 1.4,
                  boxShadow: 1
                }}
              >
                {msg.text}
              </Box>
            </Box>
          ))}
        </Stack>
        <Box 
          component="form" 
          onSubmit={handleSend} 
          sx={{ 
            display: "flex", 
            gap: 1,
            pt: 2,
            borderTop: '2px solid purple'  // Added purple top border
          }}
        >
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type your message..."
            value={message}
            onChange={e => setMessage(e.target.value)}
            sx={{
              '& .MuiOutlinedInput-root': {
                '& fieldset': { borderColor: 'purple' },
                '&:hover fieldset': { borderColor: 'purple.main' }
              }
            }}
          />
          <Button 
            type="submit" 
            variant="contained" 
            endIcon={<SendIcon />}
            sx={{
              bgcolor: 'purple',
              '&:hover': { bgcolor: 'purple.dark' }
            }}
          >
            Send
          </Button>
        </Box>
        {error && <Typography color="error" sx={{ mt: 1 }}>{error}</Typography>}
      </Paper>
    </Box>
  );
}
